setter_1(context?.variables?.obj, [ params: ["json": "{\n    \"name\": \"Jordi\"\n}"], variablePath: ["obj"], context: context]);
setProperty(context?.variables?.obj, [ params: ["property": "patient", "value": terserGet(context?.variables?.hl7, [ params: ["path": "/.PID-3"], variablePath: ["hl7"], context: context])], variablePath: ["obj"], context: context]);

