data.json_data_schema:
{
    "description": "File Input",
    "type": "object",
    "required": [
        "label",
        "directory",
        "type"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "directory": {
            "title": "Directorio",
            "type": "string"
        },
        "type": {
            "title": "Tipo",
            "type": "string",
            "enum": [
                "file",
                "file-watch",
                "aws-s3"
            ],
            "enumNames": [
                "File",
                "FileWatch",
                "aws-s3"
            ]
        },
        "file_query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {
                "charset": {
                    "type": "string",
                    "enum": [
                        "",
                        "UTF-8",
                        "UTF-16",
                        "UTF-16BE",
                        "UTF-16LE",
                        "UTF-32",
                        "UTF-32BE",
                        "UTF-32LE",
                        "ISO-8859-1",
                        "ISO-8859-2",
                        "ISO-8859-4",
                        "ISO-8859-5",
                        "ISO-8859-7",
                        "ISO-8859-9",
                        "ISO-8859-13",
                        "ISO-8859-15",
                        "windows-1250",
                        "windows-1251",
                        "windows-1252",
                        "windows-1253",
                        "windows-1254",
                        "windows-1257",
                        "CESU-8",
                        "IBM00858",
                        "IBM437",
                        "IBM775",
                        "IBM850",
                        "IBM852",
                        "IBM855",
                        "IBM857",
                        "IBM862",
                        "IBM866",
                        "KOI8-R",
                        "KOI8-U",
                        "US-ASCII",
                        "x-UTF-32BE-BOM",
                        "x-UTF-32LE-BOM"
                    ],
                    "title": "Codificación Caracteres"
                },
                "exclude": {
                    "type": "string",
                    "title": "Patron Regex para EXCLUIR archivos en la lectura."
                },
                "include": {
                    "type": "string",
                    "title": "Patron Regex para INCLUIR archivos en la lectura."
                },
                "delay": {
                    "type": "string",
                    "default": "500",
                    "title": "Tiempo Espera"
                },
                "bufferSize": {
                    "type": "number",
                    "default": 131072,
                    "title": "Tamaño de escritura del buffer (Bytes)"
                },
                "moveFailed": {
                    "type": "string",
                    "title": "Subdirectorio para dejar los errores"
                },
                "autoCreate": {
                    "type": "boolean",
                    "default": false,
                    "title": "Crear subdirectorios autom."
                },
                "delete": {
                    "type": "boolean",
                    "default": false,
                    "title": "Eliminar el archivo original"
                },
                "recursive": {
                    "type": "boolean",
                    "default": false,
                    "title": "Habilitar Busqueda recusriva."
                }
            }
        },
        "file_watch_query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {
                "events": {
                    "type": "string",
                    "title": "Eventos del directorio a escuchar.",
                    "enum": [
                        "CREATE",
                        "MODIFY",
                        "DELETE"
                    ],
                    "enumNames": [
                        "Creación de ficheros/directorios",
                        "Modificación de ficheros/directorios",
                        "Eliminación de ficheros/directorios"
                    ]
                },
                "recursive": {
                    "type": "boolean",
                    "title": "Comprobar recursivamente los directorios"
                }
            }
        },
        "extra_query_params": {
            "title": "Parámetros extra",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
