data.json_ui_schema:
{
    "label": {
        "ui:columnSize": "4"
    },
    "directory": {
        "ui:columnSize": "8"
    },
    "query_params": {
        "ui:columnSize": "12",
        "fileName": {
            "ui:columnSize": "4",
            "ui:help": "Si no se introduce sera un ID autogenerado"
        },
        "doneFileName": {
            "ui:columnSize": "4",
            "ui:help": "done-${file:name} ||| ${file:name}.done "
        },
        "bufferSize": {
            "ui:columnSize": "4"
        },
        "fileExist": {
            "ui:columnSize": "4"
        },
        "allowNullBody": {
            "ui:widget": "checkbox",
            "ui:help": "Permite que el contenido recibido sea null.",
            "ui:columnSize": "4"
        },
        "flatten": {
            "ui:widget": "checkbox",
            "ui:columnSize": "4",
            "ui:help": "Por defecto se utiliza la estructura de directorios leída (si existe) para recrearla en el destino. Si se activa se ignorará dicha estructura."
        }
    }
}

data.json_data_schema:
{
    "description": "File Output",
    "type": "object",
    "required": [
        "label",
        "directory"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "directory": {
            "title": "Directorio",
            "type": "string"
        },
        "query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {
                "fileName": {
                    "type": "string",
                    "title": "Nombre del archivo en destino "
                },
                "doneFileName": {
                    "type": "string",
                    "title": "Crear un fichero adiciónal para indicar su estado "
                },
                "bufferSize": {
                    "type": "number",
                    "default": 131072,
                    "title": "Tamaño de escritura del buffer (Bytes)"
                },
                "fileExist": {
                    "type": "string",
                    "title": " ¿ Que hacer si el archivo existe ?",
                    "enum": [
                        "Override",
                        "Append",
                        "Fail"
                    ],
                    "enumNames": [
                        "Sobreescribir",
                        "Adjuntar",
                        "Marcar como error"
                    ]
                },
                "allowNullBody": {
                    "type": "boolean",
                    "default": false,
                    "title": "Permitir contenido vacio"
                },
                "flatten": {
                    "type": "boolean",
                    "title": "Ignorar estructura directorios original"
                },
                "items": {
                    "title": "Parámetros extra",
                    "type": "array",
                    "items": {
                        "type": "object",
                        "required": [
                            "code",
                            "value"
                        ],
                        "properties": {
                            "code": {
                                "title": "Clave",
                                "type": "string"
                            },
                            "value": {
                                "title": "Valor",
                                "type": "string"
                            }
                        }
                    }
                }
            }
        }
    }
}
