data.json_data_schema:
{
    "description": "HTTP Listener",
    "type": "object",
    "required": [
        "label",
        "hostname",
        "port"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "out_format": {
            "title": "Formato Recibido",
            "type": "string",
            "enum": [
                "json",
                "hl7",
                "raw"
            ],
            "enumNames": [
                "JSON",
                "HL7",
                "RAW"
            ]
        },
        "hostname": {
            "title": "Host",
            "type": "string"
        },
        "path": {
            "title": "Ruta",
            "type": "string"
        },
        "port": {
            "title": "Puerto",
            "type": "number"
        },

        "query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {
                "async": {
                    "type": "boolean",
                    "default": false,
                    "title": "Asíncrono"
                },
                "enableCORS": {
                    "type": "boolean",
                    "default": false,
                    "title": "Habilitar CORS"
                }
            }
        },
        "extra_query_params": {
            "title": "Parámetros Extra",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
