data.json_data_schema:
{
    "description": "HTTP Client",
    "type": "object",
    "required": [
        "label",
        "hostname",
        "port"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "hostname": {
            "title": "Host",
            "type": "string"
        },
        "path": {
            "title": "Ruta",
            "type": "string"
        },
        "port": {
            "title": "Puerto",
            "type": "number"
        },

        "content_type": {
            "title": "Content Type",
            "type": "string"
        },
        "query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {
                "httpMethod": {
                    "type": "string",
                    "default": "GET",
                    "title": "Método",
                    "enum": [
                        "GET",
                        "POST",
                        "PUT",
                        "DELETE",
                        "HEAD",
                        "OPTIONS",
                        "TRACE",
                        "PATCH"
                    ]
                },
                "bridgeEndpoint": {
                    "type": "boolean",
                    "default": false,
                    "title": "Puentear Endpoint"
                },
                "clearExpiredCookies": {
                    "type": "boolean",
                    "default": true,
                    "title": "Limpiar Cookies expiradas"
                },
                "copyHeaders": {
                    "type": "boolean",
                    "default": true,
                    "title": "Copiar IN Headers"
                },
                "ignoreResponseBody": {
                    "type": "boolean",
                    "default": false,
                    "title": "Ignorar Respuesta"
                }
            }
        },

        "request_params": {
            "title": "Request",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        },
        "extra_query_params": {
            "title": "Parámetros extra",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
