data.json_data_schema:
{
    "description": "RabbitMQ Input",
    "type": "object",
    "required": [
        "label",
        "hostname",
        "port"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "hostname": {
            "title": "Host",
            "type": "string"
        },
        "port": {
            "title": "Puerto",
            "type": "number"
        },
        "queue": {
            "title": "Nombre de la cola",
            "type": "string"
        },
        "query_params": {
            "title": "Parámetros",
            "type": "object",
            "properties": {
                "username": {
                    "title": "usuario",
                    "type": "string"
                },
                "password": {
                    "title": "contraseña",
                    "type": "string"
                },
                "autoAck": {
                    "title": "autoACK",
                    "type": "boolean",
                    "default": true
                },
                "threadPoolSize": {
                    "title": "threadPoolSize",
                    "type": "number",
                    "default": 10
                },
                "autoDelete": {
                    "title": "auto delete Queue",
                    "type": "boolean",
                    "default": true
                },
                  "prefetchGlobal": {
                    "type": "string",
                    "title": "prefetchGlobal",
                    "enum": [
                        "true",
                        "false"
                    ],
                    "enumNames": [
                        "Si",
                        "No"
                    ],
                    "default": "true"
                },
                "prefetchCount": {
                    "title": "prefetchCount",
                    "type": "number",
                    "default": 10
                },
                "prefetchSize": {
                    "title": "prefetchSize",
                    "type": "number",
                    "default": 10
                }
            }
        },
        "extra_query_params": {
            "title": "Parámetros Extra",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
