data.json_data_schema:
{
    "description": "XML to JSON",
    "type": "object",
    "required": [
        "label",
        "endpoint_url",
        "output_format",
        "code"
    ],
    "properties": {
        "label": {
            "title": "Name",
            "type": "string"
        },
        "endpoint_url": {
            "title": "URL Endpoint",
            "type": "string"
        },
        "soap_action": {
            "title": "SOAP Action",
            "type": "string"
        },
        "output_format": {
            "title": "Formato salida",
            "type": "string",
            "enum": [
                "json",
                "xml"
            ],
            "enumNames": [
                "JSON",
                "XML"
            ]
        },
        "code": {
            "title": "Mensaje",
            "type": "string"
        },
        "cxf_extra_query_params": {
            "title": "Opciones CXF",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
