data.json_data_schema:
{
    "description": "TCP/MLLP Input",
    "type": "object",
    "required": [
        "label",
        "hostname",
        "port"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "out_format": {
            "title": "Formato Recibido",
            "type": "string",
            "enum": [
                "json",
                "hl7",
                "raw"
            ],
            "enumNames": [
                "JSON",
                "HL7",
                "RAW"
            ]
        },
        "protocol": {
            "title": "Protocolo",
            "type": "string",
            "enum": [
                "netty:tcp",
                "mllp"
            ],
            "enumNames": [
                "TCP Netty",
                "MLLP"
            ]
        },
        "hostname": {
            "title": "Host",
            "type": "string"
        },
        "port": {
            "title": "Puerto",
            "type": "number"
        },
        "mllp_query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {
                "autoAck": {
                    "type": "boolean",
                    "default": true,
                    "title": "Auto Ack"
                },
                "charsetName": {
                    "type": "string",
                    "enum": [
                        "",
                        "UTF-8",
                        "UTF-16",
                        "UTF-16BE",
                        "UTF-16LE",
                        "UTF-32",
                        "UTF-32BE",
                        "UTF-32LE",
                        "ISO-8859-1",
                        "ISO-8859-2",
                        "ISO-8859-4",
                        "ISO-8859-5",
                        "ISO-8859-7",
                        "ISO-8859-9",
                        "ISO-8859-13",
                        "ISO-8859-15",
                        "windows-1250",
                        "windows-1251",
                        "windows-1252",
                        "windows-1253",
                        "windows-1254",
                        "windows-1257",
                        "CESU-8",
                        "IBM00858",
                        "IBM437",
                        "IBM775",
                        "IBM850",
                        "IBM852",
                        "IBM855",
                        "IBM857",
                        "IBM862",
                        "IBM866",
                        "KOI8-R",
                        "KOI8-U",
                        "US-ASCII",
                        "x-UTF-32BE-BOM",
                        "x-UTF-32LE-BOM"
                    ],
                    "title": "Codificación Caracteres"
                },
                "keepAlive": {
                    "type": "boolean",
                    "default": true,
                    "title": "Mantener viva la conexión"
                },
                "requireEndOfData": {
                    "type": "boolean",
                    "default": true,
                    "title": "Requerir EndOfData"
                },
                "stringPayload": {
                    "type": "boolean",
                    "default": true,
                    "title": "Convertir Payload String"
                },
                "validatePayload": {
                    "type": "boolean",
                    "default": false,
                    "title": "Validar Payload HL7"
                },
                "acceptTimeout": {
                    "type": "number",
                    "default": 60000,
                    "title": "Timeout espera conexión (servidor)"
                },
                "backlog": {
                    "type": "number",
                    "default": 5,
                    "title": "Tamaño de la cola de conexiones"
                }
            }
        },
        "netty_query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {
                "encoding": {
                    "type": "string",
                    "enum": [
                        "CESU-8",
                        "IBM00858",
                        "IBM437",
                        "IBM775",
                        "IBM850",
                        "IBM852",
                        "IBM855",
                        "IBM857",
                        "IBM862",
                        "IBM866",
                        "ISO-8859-1",
                        "ISO-8859-2",
                        "ISO-8859-4",
                        "ISO-8859-5",
                        "ISO-8859-7",
                        "ISO-8859-9",
                        "ISO-8859-13",
                        "ISO-8859-15",
                        "KOI8-R",
                        "KOI8-U",
                        "US-ASCII",
                        "UTF-8",
                        "UTF-16",
                        "UTF-16BE",
                        "UTF-16LE",
                        "UTF-32",
                        "UTF-32BE",
                        "UTF-32LE",
                        "x-UTF-32BE-BOM",
                        "x-UTF-32LE-BOM",
                        "windows-1250",
                        "windows-1251",
                        "windows-1252",
                        "windows-1253",
                        "windows-1254",
                        "windows-1257"
                    ],
                    "title": "Codificación Caracteres"
                },
                "disconnect": {
                    "type": "boolean",
                    "default": false,
                    "title": "Desconectar el socket cada uso"
                },
                "keepAlive": {
                    "type": "boolean",
                    "default": true,
                    "title": "Mantener viva la conexión"
                },
                "sync": {
                    "type": "boolean",
                    "default": true,
                    "title": "Sincronizar (OneWay)"
                },
                "clientMode": {
                    "type": "boolean",
                    "default": false,
                    "title": "Modo Cliente"
                },
                "reconnect": {
                    "condition": "netty_query_params.clientMode=true",
                    "type": "boolean",
                    "default": true,
                    "title": "Reconectar Modo Cliente"
                },
                "reconnectInterval": {
                    "condition": "netty_query_params.clientMode=true",
                    "type": "number",
                    "default": 10000,
                    "title": "Intervalo Reconexión Modo Cliente"
                }
            }
        },
        "extra_query_params": {
            "title": "Parámetros Extra",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
