data.json_data_schema:
{
    "description": "Create simple JSON or XML objects from input message.",
    "type": "object",
    "required": [
        "label",
        "format"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "format": {
            "title": "Formato",
            "type": "string",
            "enum": [
                "json",
                "xml"
            ],
            "enumNames": [
                "JSON",
                "XML"
            ]
        },
        "code_json": {
            "title": "JSON",
            "type": "string"
        },
        "code_xml": {
            "title": "XML",
            "type": "string"
        }
    }
}
